  TRIM(
    IF(
      FIND(",", ARRAYJOIN({fldthjxEAmKawmX5I}, ", ")),
      LEFT(
        ARRAYJOIN({fldthjxEAmKawmX5I}, ", "),
        FIND(",", ARRAYJOIN({fldthjxEAmKawmX5I}, ", ")) - 1
      ),
      ARRAYJOIN({fldthjxEAmKawmX5I}, ", ")
    )
  )
)

IF(
  FIND(",", ARRAYJOIN({fldthjxEAmKawmX5I}, ", ")),
  TRIM(
    REGEX_EXTRACT(
      ARRAYJOIN({fldthjxEAmKawmX5I}, ", "),
      ", ([^,]+)"
    )
  ),
  BLANK()
)

IF(
  LEN(ARRAYJOIN({fldthjxEAmKawmX5I}, ", ")) - LEN(SUBSTITUTE(ARRAYJOIN({fldthjxEAmKawmX5I}, ", "), ",", "")) >= 2,
  TRIM(
    REGEX_EXTRACT(
      ARRAYJOIN({fldthjxEAmKawmX5I}, ", "),
      "(?:[^,]+, ){2}([^,]+)"
    )
  ),
  BLANK()
)
